export default {
  data() {
    return {
      formula: "",
      formulaAlert: false
    };
  },
  methods: {
    evaluate(num) {
      try {
        /* WARNING! Never use eval in production code. */

        let result = eval(this.formula);
        this.formulaAlert = false;
        this.formula = result;
      } catch (exception) {
        this.formulaAlert = true;
      }
    },
        append(num) {
        if (num==null){ 
        this.formula="";
        } else { 
          this.formula+=num;
        }
    }
  }
};
